import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import logo from "./assets/Logo.png";

function App() {

  const [contestants, setContestants] = useState([]);
  const [voted, setVoted] = useState(false);

  /* ---------- FETCH DATA ---------- */

  const fetchData = async () => {
    try{
      const res = await axios.get("http://localhost:5000/contestants");
      setContestants(res.data);
    }
    catch(err){
      console.log(err);
    }
  };

  /* ---------- AUTO REFRESH ---------- */

  useEffect(() => {

    fetchData();

    const interval = setInterval(fetchData, 3000);

    return () => clearInterval(interval);

  }, []);

  /* ---------- VOTE FUNCTION ---------- */

  const vote = async (id) => {

    if(voted) return;

    try{
      await axios.post(`http://localhost:5000/vote/${id}`);

      localStorage.setItem("voted","true");
      setVoted(true);

      fetchData();

      alert("Vote submitted!");
    }
    catch(err){
      console.log(err);
    }
  };

  /* ---------- SORT LEADERBOARD ---------- */

  const sortedContestants = [...contestants].sort(
    (a,b) => b.votes - a.votes
  );

  /* ---------- UI ---------- */

  return (
    <div className="container">

      <img src={logo} alt="GEC Talent Spotlight" className="logo"/>

      <div className="cards-wrapper">

        {sortedContestants.map((c,index) => (
          <div key={c._id} className="card">

            <img
              src={c.image}
              alt={c.name}
              className="avatar"
              onError={(e)=>{e.target.src="/images/default.png"}}
            />

            <h3>{c.name}</h3>

            <p>Votes: {c.votes}</p>

            <button
              disabled={voted}
              onClick={() => vote(c._id)}
            >
              {voted ? "Already Voted" : "Vote"}
            </button>

          </div>
        ))}

      </div>

    </div>
  );
}

export default App;
