import { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";
import logo from "./assets/Gec.png";

function App() {
  const [contestants, setContestants] = useState([]);
  const [voted, setVoted] = useState(false);

  useEffect(() => {
    axios.get("http://localhost:5000/contestants")
      .then(res => setContestants(res.data));

    if(localStorage.getItem("voted")){
      setVoted(true);
    }
  }, []);

  const vote = async (id) => {
  if (voted) return;

  await axios.post(`http://localhost:5000/vote/${id}`);

  localStorage.setItem("voted", "true");
  setVoted(true);

  // fetch updated vote counts
  const res = await axios.get("http://localhost:5000/contestants");
  setContestants(res.data);

  alert("Vote submitted!");
};

  return (
    <div className="container">
      <img src={logo} alt="GEC Talent Spotlight" className="logo"/>

      {contestants.map(c => (
        <div key={c._id} className="card">
          <h3>{c.name}</h3>
          <p>Votes: {c.votes}</p>

          <button disabled={voted} onClick={() => vote(c._id)}>
            {voted ? "Already Voted" : "Vote"}
          </button>
        </div>
      ))}
    </div>
  );
}

export default App;
