import React from 'react'
import { useEffect, useState } from "react";
import axios from "axios";
function Admin() {
  const [contestants, setContestants] = useState([]);
  const [voted, setVoted] = useState(false);
  useEffect(() => {
      fetchData();
    }, []);

  const fetchData = async () => {
        const res = await axios.get("http://localhost:5000/contestants");
        setContestants(res.data);
    };
  
  function removeHandler(id){
    console.log("Remove Handeler working"+id)
  }
  return (
    <div style={{color:'white'}}>
      <h1 style={{color:'white',textAlign:'center'}}>Welcome to Admin Page</h1>
      <div>
        <h3>Add Contestent</h3>
        <form>
          <label htmlFor="name">Name</label>
          <input id='name' type='text'/><br />
          <label htmlFor="image">Image Link</label>
          <input type="text" id='image' /><br />
          <button>Add</button>
        </form>
      </div>
      <div>
        <h3>Manage Contestent</h3>
        <div className="container">
          <div className="cards-wrapper">
              {contestants.map(c => (
              <div key={c._id} className="card">
  
                  <img src={c.image} alt={c.name} className="avatar" />
  
                  <h3>{c.name}</h3>
                  <p>Votes: {c.votes}</p>
                  <button onClick={()=>removeHandler(c._id)}>Remove</button>
              </div>
              ))}
          </div>
        </div>
      </div>
    </div>
  )
}

export default Admin
